mapper.select:
select idx, user_idx, writer, title, content, views, crea_time 
from tbl_board
where idx = #{idx} and writer = #{writer}

select board_file_path
from tbl_board_file
where board_idx = #{idx} and writer = #{writer}

select thumbnail_file_path
from tbl_user_prf
where user_idx = #{user_idx}

select board_image_file_path
from tbl_board_image_file
where board_idx = #{idx} and writer = #{writer}

mapper.update:
update tbl_board 
set title = #{title}, content = #{content}
where idx = #{board_idx} and user_idx = #{user_idx} and writer = #{writer}
